mapper.select:
SELECT COUNT(1)
  FROM MEMBERS
 WHERE EMAIL = #{_parameter}

mapper.insert:
INSERT INTO MEMBERS 
 (EMAIL
, NAME
, PASSWORD
, SALT
, LOGIN_FAIL_COUNT)
VALUES
 (#{email}
, #{name}
, #{password}
, #{salt}
, 0)

mapper.select:
        SELECT SALT
		  FROM MEMBERS
		 WHERE EMAIL = #{_parameter}

mapper.update:
    UPDATE MEMBERS
	   SET LOGIN_FAIL_COUNT = LOGIN_FAIL_COUNT + 1
	     , LATEST_LOGIN_FAIL_DATE = SYSDATE
	     , LATEST_LOGIN_IP = #{ip}
	 WHERE EMAIL = #{email}

        UPDATE MEMBERS
		   SET LOGIN_FAIL_COUNT = 0
		     , LATEST_LOGIN_FAIL_DATE = NULL
		     , LATEST_LOGIN_IP = #{ip}
		     , LATEST_LOGIN_SUCCESS_DATE = SYSDATE
		 WHERE EMAIL = #{email}
